INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'Watch any movie as many times as you want. All you need to get started is a proper internet connection. Watch your movie instantly in HD and with subtitles. If the movie is out there, Time 4 Popcorn will find the best version possible and start streaming it right away.', 'Regardez n''importe quel film autant de fois que vous le souhaitez. Tout ce dont vous avez besoin est une bonne connexion Internet. Regardez votre film en HD instantanément avec ou sans sous-titres. Si le film est disponible sur les réseaux Torrent, Time 4 Popcorn trouvera la meilleure version possible.', 'http://www.time4popcorn.eu/', 73);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'Opera is a Web browser that offers lots of features to let you take advantage of today''s Web. Popular features: Opera Turbo speeds up browsing on slow connections. Opera Link can synchronize bookmarks with other computers and mobile phones.', 'Opera est un navigateur Web qui offre de nombreuses fonctionnalités pour vous permettre de profiter du Web d''aujourd''hui. Parmi les fonctionnalités populaires: Opera Turbo accélère la navigation sur les connexions lentes. Opera Link permet de synchroniser vos favoris avec d''autres ordinateurs ou téléphones mobiles.', 'http://www.opera.com/', 74);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'Access TV replays from a single point through Captvty to catch up with various TV channels programs.', 'Accédez, depuis un point unique grâce à Captvty, aux directs et à une multitude d''émissions proposées au rattrapage par différentes chaînes de télévision.', 'http://captvty.fr/', 75);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'Tor is free software and an open network that helps you defend against a form of network surveillance that threatens personal freedom and privacy, confidential business activities and relationships, and state security known as traffic analysis.', 'Tor est un logiciel et réseau ouvert libre qui vous aide à vous défendre contre une forme de surveillance du réseau qui menace les libertés individuelles et votre vie privée. Son utilisation vous prémunit contre les activités commerciales et tout ce qui touche à l''analyse du trafic.', 'https://www.torproject.org/', 76);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'QuickTime features advanced video compression technology called H.264 to deliver brilliant, crisp HD video using less bandwidth and storage.', 'QuickTime intègre la technologie avancée de compression vidéo H.264 pour des vidéo HD qui utilisent moins de bande passante et de stockage sans pour autant sacrifier la qualité.', 'http://www.apple.com/quicktime/', 77);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'TeamViewer is a solution for remote control, desktop sharing, and file transfer that works behind any firewall and NAT proxy.', 'TeamViewer est une solution pour le contrôle à distance, le partage de bureau et le transfert de fichier qui fonctionne derrière n''importe quel pare-feu et proxy NAT.', 'http://www.teamviewer.com/', 78);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'KMPlayer is all in one media player, covering various formats such as VCD, DVD, AVI, MKV, Ogg Theora, OGM, 3GP, MPEG-1/2/4, WMV, RealMedia, QuickTime. It has an extra feature to play incomplete or damaged AVI file, Locked Media Files while downloading or sharing.', 'KMPlayer est un lecteur multimédia tout-en-un, couvrant divers formats tels que VCD, DVD, AVI, MKV, Ogg Theora, OGM, 3GP, MPEG-1/2/4, WMV, RealMedia, QuickTime. Il a une fonction supplémentaire pour lire un fichier AVI incomplet ou endommagé ainsi que les fichiers multimédia bloqués en cours de téléchargement ou de partage.', 'http://www.kmplayer.com/', 79);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'Audacity is an open source, cross-platform audio editor and recorder. Audacity can record and play sounds and import and export WAV, AIFF, MP3, and OGG files.', 'Audacity est un éditeur et enregistreur audio multi-plateforme libre. Audacity peut enregistrer et lire des sons, les importer et exporter aux formats WAV, AIFF, MP3 et OGG.', 'http://audacity.sourceforge.net/', 80);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'RoboForm makes logging into Web sites and filling forms faster, easier, and more secure. RoboForm memorizes and securely stores each user name and password the first time you log into a site, then automatically supplies them when you return.', 'RoboForm se charge de vous authentifier à des sites Web et de préremplir les formulaires, pour une navigation plus facile et plus sûr. RoboForm mémorise et stocke en toute sécurité chaque nom d''utilisateur et mot de passe la première fois que vous vous connectez à un site, puis les fournit automatiquement lorsque vous revenez.', 'http://www.roboform.com/', 81);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'USB Disk Security provides protection against any malicious programs trying to attack via USB drive.', 'USB Disk Security assure une protection contre les programmes malveillants qui tentent d''attaquer votre ordinateur via le port USB.', 'http://www.zbshareware.com/', 82);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'SyncBackPro is an advanced file backup & synchronization program that can be used with hard drives, removable media, email servers, network shares, and cloud storage services.', 'SyncBackPro est un programme de sauvegarde et de synchronisation de fichiers qui peut être utilisé avec des disques durs, des disques amovibles, des serveurs email, des partages réseau et des services de stockage type cloud.', 'http://www.2brightsparks.com/syncback/sbpro.html', 83);
