INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'ESET NOD32 Antivirus features new advanced technology that protects against threats as well as browser and application exploits. Anti-Phishing module protects you from fake websites trying to acquire your personal information. The new Social Media Scanner extends security to your Facebook and Twitter accounts by checking for malicious content.', 'ESET NOD32 Antivirus dispose d''une nouvelle technologie de pointe qui vous protège contre les menaces et l''exploitation des failles de votre navigateur et de vos applications. Un module anti-hameçonnage vous protège contre les faux sites Web qui tentent d''acquérir vos informations personnelles. Le nouveau scanner de médias sociaux étend la sécurité à vos comptes Facebook et Twitter en vérifiant le contenu malveillant.', 'http://www.eset.com/int/home/products/antivirus/', 88);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'Daum Potplayer is a free Multimedia player that supports a variety of different video codecs and formats. Potplayer already includes built in codecs, eliminating the need for manual installation.', 'Daum PotPlayer est un lecteur multimédia gratuit qui prend en charge une grande variété de codecs et formats vidéo. Potplayer intègre déjà les codecs, éliminant le besoin de les installer manuellement.', 'http://potplayer.daum.net/', 89);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'Auslogics Disk Defrag Touch is a unique tool made specifically for touch-screen computers. Built on the same powerful defragmentation engine as in Auslogics'' popular Disk Defrag, the new program is just as fast and effective at fighting file fragmentation.', 'Auslogics Disk Defrag Touch est un outil conçu spécialement pour les ordinateurs à écran tactile. Construit sur le même moteur puissant de défragmentation que le populaire Auslogics Disk Defrag, ce nouveau défragmenteur est tout aussi rapide et efficace pour combattre la fragmentation de vos fichiers.', 'http://www.auslogics.com/en/software/disk-defrag-touch/', 90);
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, website, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'UltraDefrag is an open source defragmentation tool. Its engine is very fast, because it is created as kernel-mode driver. UltraDefrag reduces the file fragmentation, thus it makes disk operations faster.', 'UltraDefrag est un outil de défragmentation open-source. Son moteur est très rapide, car il est se base sur le kernel-mode driver. UltraDefrag réduit la fragmentation des fichiers, rendant ainsi les opérations sur le disque plus rapides.', 'http://ultradefrag.sourceforge.net/', 91);
