INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'CPU-Z gives you advanced information on your processor.', 'CPU-Z donne des informations avances sur votre processeur.', 121);
