INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'Docker is an open platform for developers and sysadmins to build, ship, and run distributed applications, whether on laptops, data center VMs, or the cloud.', 'Docker est une plate-forme pour les dveloppeurs et les administrateurs systme pour construire, livrer et excuter des applications distribues, que ce soit sur les ordinateurs portables, des data center virtuels ou dans le cloud.', 122);
-- Dropbox
INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'Dropbox works just like any other folder on your computer, but with a few differences. Any files or folders inside Dropbox will get synchronized to Dropbox''s servers and any other computer linked to your account.', 'Dropbox fonctionne comme n''importe quel autre dossier sur votre ordinateur, mais avec quelques diffrences. Tous les fichiers ou dossiers  l''intrieur de votre Dropbox vont automatiquement se synchroniser sur les serveurs de Dropbox et aussi sur tout autre ordinateur connect  votre compte.', 123);
