INSERT INTO applicationdescription(id, creationdate, dbversion, updatedate, descriptionen, descriptionfr, application_id) VALUES (nextval('application_description_seq'), now(), 0, now(), 'PowerISO is a powerful image file processing tool, which allows you to open, extract, burn, create, edit, compress, encrypt, split and convert ISO files, and mount ISO files with internal virtual drive.', 'PowerISO est un utilitaire pour grer vos fichiers image en vous permettant d''ouvrir, extraire, graver, crer, diter, compresser, crypter, splitter et convertir des fichiers ISO. Il permet aussi de monter vos images sur des lecteurs virtuels internes.', 125);
-- Nero Burning ROM
